INSERT INTO `dive_store` VALUES ('3f7760e1-4a49-4226-9218-e397267c50de', '2016-05-17 10:27:37', '亚洲_泰国', 'uploadfiles/store/store_eVMt1465269914215.jpg', '泰国-龟岛潜水之家度假酒店', '', '', '✈Ban’s Diving Resort ', '<h1 style=\"text-align:center;\">\r\n	<span style=\"color:#337FE5;\"><img src=\"/attached/image/2016/06/07/f7492868-d13b-4f93-9c28-f62f4f429c36.jpg\" alt=\"\" /><br />\r\n</span> \r\n</h1>\r\n<h1 style=\"text-align:center;\">\r\n	<span style=\"color:#337FE5;\">Ban´s Diving Resort&nbsp;</span><br />\r\n</h1>\r\n<p>\r\n	<span style=\"line-height:36px;\"><span style=\"font-family:\'Microsoft YaHei\';line-height:1;\"><strong><span style=\"color:#333333;font-family:NSimSun;\"><br />\r\n</span></strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:36px;\"><span style=\"font-family:\'Microsoft YaHei\';line-height:1;\"><strong><span style=\"color:#333333;font-family:NSimSun;\">度假村名称：</span></strong><span style=\"color:#333333;font-family:NSimSun;\"><span style=\"color:#64451D;font-family:NSimSun;\">龟岛潜水之家度假酒店</span></span></span></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;font-family:NSimSun;\">地点：</span></strong><span style=\"color:#333333;\"></span><span style=\"color:#64451D;font-family:NSimSun;\">泰国、涛岛</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><span style=\"color:#333333;font-family:NSimSun;\">开业时间</span></b><span style=\"color:#333333;font-family:NSimSun;\"></span><span style=\"color:#64451D;font-family:NSimSun;\"><span style=\"font-family:NSimSun;\">：</span><span style=\"line-height:12px;font-family:NSimSun;\">暂无</span></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><b><span style=\"color:#333333;font-family:NSimSun;\">星级：</span></b></b><span style=\"color:#64451D;font-family:NSimSun;\">4</span><span style=\"color:#64451D;font-family:NSimSun;\">星</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><span style=\"color:#333333;font-family:NSimSun;\">交通信息：</span></b><span style=\"color:#333333;font-family:NSimSun;\"></span><span style=\"font-family:NSimSun;color:#64451D;\"><span style=\"color:#64451D;font-family:NSimSun;\">曼谷飞苏梅岛，再乘船上岛（船程1小时左右），码头距度假村20分钟</span></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><b><span style=\"color:#333333;font-family:NSimSun;\">教授语言</span></b><span style=\"color:#999999;\"></span><span style=\"color:#333333;font-family:NSimSun;\">：</span></b><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\"><span style=\"font-family:NSimSun;\">英语、中文</span></span> \r\n</p>\r\n<span style=\"line-height:1.5;color:#64451D;\"><span style=\"font-family:NSimSun;\"> \r\n<hr />\r\n</span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span> \r\n<p>\r\n	<span style=\"line-height:36px;color:#999999;font-family:NSimSun;\"><span style=\"line-height:1;color:#333333;font-family:NSimSun;\"><strong>其它信息</strong></span></span><span style=\"line-height:36px;color:#999999;\"><span style=\"line-height:1;color:#333333;font-family:NSimSun;\"><strong></strong><span style=\"color:#64451D;font-family:NSimSun;\">：</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">Ban\'s潜水度假村坐落在美丽的海滩，你可以在这里度过轻松美丽的假期，</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">Ban\'s</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">是</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">PADI</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">五星级潜水教学中心（</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">IDC)</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">，提供全系列的</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">PADI</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">课程以及各种潜水项目。事实上，在泰国有很多的潜水店老板和教练都是从</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">Ban\'s</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">学会了潜水。</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">Ban\'s</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">是唯一获得</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">PADI\"</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">教练培训杰出贡献奖（</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">Outstanding Contribution to Instructor Development</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">）</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">\"</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">的培训中心（</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">IDC</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">），当然，</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">Ban\'s</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">也曾获得过很多其他的</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">PADI</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">奖项：</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">\"Support to the PADI\r\nSystem\" </span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">（</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">2000-2002</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">）和</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\"> \"Hallmark of Excellence\" </span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">（</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">1998-1999</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">）。</span><span style=\"color:#64451D;line-height:1.5;font-family:NSimSun;\">Ban\'s的住宿条件可以算岛上潜店最好的。 --有3间餐厅和2个室外游泳池，水疗和美发沙龙、24小时便利店、2个游泳池和鱼碗酒吧。 --提供带空调和私人阳台的套房。出租单人划艇、水下呼吸潜游、潜水游等。\r\n--由当地家庭经营，还保持着非常特别的家庭式待客之道，让您有家的感觉。</span>\r\n</p>\r\n<hr />\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/06/07/caf61b4e-4a1c-4605-8190-15bcd0708e4f.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/06/07/dff0fd53-775c-4e48-adbd-2172ed3b06e9.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/06/07/e8ba273b-2080-42fc-8d06-38e482710f9a.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/06/07/feacc931-d939-483f-909c-7748397ea320.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/06/07/831a37ca-668b-48cb-bca7-49c0d9332688.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/06/07/82413fe3-1e30-487b-be21-782d416cb4bc.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/06/07/f98d4858-c9f1-43e8-9b4f-7d9ee7bf7148.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/06/07/e4a0c58c-205b-451a-b563-6