INSERT INTO `dive_store` VALUES ('63c48c78-5614-4a17-b397-db54f1550c19', '2016-06-24 15:54:36', '南美_巴西_巴伊亚', 'uploadfiles/store/store_Ey7c1466756153288.jpg', '巴西-马布科斯塔巴西利斯度假酒店', '', '', '✈ Costa Brasilis Resort', '<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/4f74641c-0359-4624-b125-513d72be393d.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:24px;font-family:NSimSun;color:#337FE5;\"><strong>Costa Brasilis Resort</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-family:NSimSun;color:#333333;\"><strong><br />\r\n</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-family:NSimSun;color:#333333;\"><strong>度假村名称：</strong></span><span style=\"line-height:1.5;font-family:NSimSun;color:#64451D;\">马布科斯塔巴西利斯度假酒店</span>\r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#333333;\"><strong>地点：</strong></span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">巴西 巴伊亚州 桑塔克鲁茨卡巴利亚</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#333333;\"><strong>开业时间：</strong></span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">暂无</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#333333;\"><strong>星级</strong></span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">：5星</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"line-height:1.5;font-family:NSimSun;color:#64451D;\"><span style=\"color:#333333;\"><strong>交通信息：</strong></span>距离</span><span style=\"line-height:1.5;font-family:NSimSun;color:#64451D;\">古罗港机场（Porto Seguro Airport）30公里</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#333333;\"><strong>教授语言</strong></span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">：英语、中文</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong>其它信息：</strong> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-family:NSimSun;color:#64451D;\">酒店位于大西洋（Atlantic）海岸的1个僻静区域，可直接通往海滩，提供室外游泳池、网球场以及覆盖公共区域的免</span><span style=\"line-height:1.5;font-family:NSimSun;color:#64451D;\">费网络连接。所有客房均设有空调、有线电视、迷你吧和保险箱。客房均享有大西洋（Atlantic Ocean）的全景。</span><span style=\"line-height:1.5;font-family:NSimSun;color:#64451D;\">Restaurant Caravelas餐厅供应融合了当地巴伊亚（Bahian）美食和国际美食特色的菜式。酒店还设有池畔酒吧，供客</span><span style=\"line-height:1.5;font-family:NSimSun;color:#64451D;\">人享用鸡尾酒和小吃。度假酒店提供一系列娱乐设施，包括室外游泳池、网球场、健身中心以及带台球桌的游戏室。</span><span style=\"line-height:1.5;font-family:NSimSun;color:#64451D;\">酒店还设有1间现代Spa中心，提供桑拿和按摩理疗服务。</span> \r\n</p>\r\n<hr />\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/1dff6116-d4c0-43b8-a9bc-80772861cc5b.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/ce1e2549-17de-4a5b-becd-53de77d933ce.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/e6fff2de-6976-4a03-b6f3-df489883c796.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/08597d32-f7e9-4cad-8018-a2f901e61c51.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/ca9876af-d2d6-4fba-a66f-7e1ec7acde26.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/7015bea6-f118-4f79-8ff7-0924d4eda9d6.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/8e9097cd-2246-478d-8366-5f39e0735b7e.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/a987bf6c-7837-4efe-890c-197e6619f938.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/2243e8ac-2324-4f7a-88ae-912d49f4cd98.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/0db361e4-52e1-47aa-ae52-b0b04ab6aa3a.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/298835c7-032a-4c5b-9edd-ed128e509bbf.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/675c44e2-5be0-4f24-902c-0055a90ca5fb.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/b8353f37-0cb1-4348-a40c-6bea704b803a.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/aee3b1e3-79e5-448b-99f0-2b0425db28d3.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/c04aff1a-24d8-4ed3-ba51-c53a7b638b68.jpg\" alt=\"\" />\r\n</p>', '01768d64-6c80-4f7c-84dc-ce3b26064a1c', '0');
