INSERT INTO `dive_store` VALUES ('7525e242-4bc9-466b-bb54-c72575833a09', '2016-01-29 17:50:33', '大洋洲_密克罗尼西亚', 'uploadfiles/store/store_RHYt1454062935533.jpg', '密克罗尼西亚-亚普太平洋潜水度假村', '', '', '✈Yap Pacific Dive Resort', '<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"></span> \r\n</div>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"color:#333333;\"><img src=\"/attached/image/2016/06/16/15c2bc7b-7e5f-4613-901b-a8c014e88d58.jpg\" alt=\"\" /><br />\r\n</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"color:#333333;\"><span style=\"color:#337FE5;font-size:24px;\"></span><span style=\"color:#337FE5;font-size:24px;font-family:NSimSun;\">Yap Pacific Dive Resort</span><span style=\"color:#337FE5;font-size:24px;\"></span><br />\r\n</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#333333;\"><br />\r\n</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#333333;font-family:NSimSun;\">度假村名称</span></strong><span style=\"color:#333333;\"><span style=\"font-family:NSimSun;\">：</span><span style=\"color:#64451D;font-family:NSimSun;\">亚普太平洋潜水度假村</span><span style=\"color:#64451D;\"></span></span> \r\n</p>\r\n<hr />\r\n<strong><span style=\"color:#333333;font-family:NSimSun;\">地点：</span></strong><span style=\"color:#333333;\"></span><span style=\"color:#64451D;font-family:NSimSun;\">密克罗尼西亚 &nbsp;亚普</span> \r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;font-family:NSimSun;\">开业时间：</span></strong><span style=\"color:#64451D;font-family:NSimSun;\">2010</span> \r\n</p>\r\n<hr />\r\n<strong><span style=\"color:#333333;font-family:NSimSun;\">星级</span></strong><span style=\"color:#333333;font-family:NSimSun;\">：</span><span style=\"color:#64451D;font-family:NSimSun;\">4星</span><span></span> \r\n<hr />\r\n<strong><span style=\"color:#333333;font-family:NSimSun;\">交通信息：</span></strong><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">距离亚普国际机场（Yap International Airport）有6.6公里</span>\r\n<hr />\r\n<strong><span style=\"color:#333333;font-family:NSimSun;\">教授语言</span><span style=\"font-family:NSimSun;\">：</span></strong><span style=\"color:#64451D;font-family:NSimSun;\">英语、中文</span><span></span> \r\n<hr />\r\n<span></span> \r\n<p>\r\n	<strong><span style=\"color:#333333;font-family:NSimSun;\">其它信息：</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;\"><span style=\"color:#64451D;font-family:NSimSun;\">亚普太平洋潜水度假村为商务和休闲旅游游客而设计，位于得天独厚的亚普地区，是本市最受欢迎的酒店之一。</span></span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">Yap Pacific Dive Resort度假酒店俯瞰着查莫洛湾（Chamarro Bay），周围环绕着热带花园，距离生活艺术博物馆（Living Art Museum）、石钱公园（Stone Money Park）和市中心仅有200米。</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">游客可以尽情领略市区内的迷人风景。</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">共有22间房间可供客人选择，全部都给人以安静典雅的感觉。 酒店内的室</span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">外游泳池, 水疗, 按摩, 花园, 潜水项目是忙碌的一天后放松身心的理想去处。 </span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">客人可在阳光明媚的露台享受海风拂面，或骑自行车游览该地区。旅游咨询台可以安排浮潜、皮划艇和钓鱼活动，亦可应要求安排卡拉OK活动。客房采用独特的1880年代维多利亚式建筑结构。Traders\' Lounge &amp; Grill享有海景，是享用休闲饮料的理想场所。</span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">Yap Pacific Dive Resort是来亚普探索城市魅力，放松身心的理想住处！</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/16/c0e9702e-cd07-44ba-9b96-df7795e217e7.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/01/29/220f4db2-485e-4398-9585-a36857913e98.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/06/16/de338f7e-abd4-4547-aa76-7fd410ed912d.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/01/29/c1c76005-a431-413e-a59a-faca8a022cbe.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/01/29/ca8a2e43-da5a-4f26-977f-4518ba3e201c.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"></span> \r\n</p>\r\n<span style=\"color:#64451D;\"></span> \r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/16/6fae395a-5747-4990-8996-95cb97607f16.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/16/815e6066-cdc0-4146-a88e-b7fd5594ef97.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/16/82f528da-5ebc-4046-9499-c3ca7d7b51b4.jpg\" alt=\"\" /> \r\n</p>', '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '0');
