INSERT INTO `dive_store` VALUES ('7c40e417-6f39-4fb9-9a33-ce8b10c91448', '2016-06-20 16:33:48', '非洲_毛里求斯', 'uploadfiles/store/store_TPsK1466412952005.jpg', '毛里求斯-比奇科默特鲁奥克斯海滩度假村及水疗中心', '', '', '✈ Beachcomber Trou aux Biches Resort & Spa', '<p class=\"p\" style=\"text-align:center;margin-left:0pt;text-indent:0pt;\">\r\n	<strong><span style=\"color:#333333;\"><span style=\"font-size:24px;font-family:NSimSun;color:#337FE5;\"><img src=\"/attached/image/2016/06/20/6a8e8765-fc25-4bc9-b1e8-fcd262f9e01b.jpg\" alt=\"\" /><br />\r\n</span></span></strong> \r\n</p>\r\n<p class=\"p\" style=\"text-align:center;margin-left:0pt;text-indent:0pt;\">\r\n	<strong><span style=\"color:#333333;\"><span style=\"font-size:24px;font-family:NSimSun;color:#337FE5;\">Beachcomber Trou aux Biches Resort &amp; Spa</span><span style=\"font-size:24px;font-family:NSimSun;color:#337FE5;\"></span><br />\r\n</span></strong> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:0.0000pt;text-indent:0.0000pt;\">\r\n	<strong><span style=\"color:#333333;\"><br />\r\n</span></strong> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:0.0000pt;text-indent:0.0000pt;\">\r\n	<strong><span style=\"color:#333333;\">度假村名称：</span></strong><span style=\"color:#333333;\"><span style=\"color:#64451D;\">比奇科默特鲁奥克斯海滩度假村及水疗中心</span><span style=\"color:#64451D;\"></span></span> \r\n</p>\r\n<h1 class=\"item\" style=\"text-align:center;font-size:1.8em;color:#003580;font-family:Helvetica, sans-serif;background-color:#FFFFFF;\">\r\n</h1>\r\n<hr />\r\n<b> \r\n<p>\r\n	<b><span style=\"color:#333333;font-family:NSimSun;\">地点：</span></b><span style=\"font-weight:normal;line-height:1.5;color:#64451D;font-family:NSimSun;\">毛里求斯 特鲁欧科比赫斯</span> \r\n</p>\r\n</b> \r\n<hr />\r\n<p>\r\n	<b><span style=\"color:#333333;font-family:NSimSun;\">开业时间：</span></b><span style=\"color:#64451D;font-family:NSimSun;\">2010年</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><span style=\"color:#333333;font-family:NSimSun;\">星级：</span></b><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">5星</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><span style=\"color:#333333;font-family:NSimSun;\">交通信息：</span></b><span style=\"line-height:1.5;color:#64451D;\">距离机场70公里，距离首都路易港（Port Louis）20公里</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><span style=\"color:#333333;font-family:NSimSun;\">教授语言：</span></b><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">英语、中文</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><span style=\"color:#333333;font-family:NSimSun;\">其它信息：</span></b> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;\">这间生态友好的度假酒店坐落于Trou aux Biches海滩上，四周环绕着热带花园，提供有带家具的阳台和露台的空调住</span><span style=\"color:#64451D;line-height:1.5;\">宿。部分住宿还设有私人游泳池。宽敞明亮的套房和别墅均设有带卫星平面电视的休息区、沏茶/咖啡设施和带双人盥</span><span style=\"color:#64451D;line-height:1.5;\">洗池的现代浴室。部分住宿设有设施齐全的厨房和洗洗衣机。Beachcomber度假酒店设有6间餐厅，包括池畔酒吧和海</span><span style=\"color:#64451D;line-height:1.5;\">滩酒吧，亦提供全天客房服务。客人可在沙滩上或游泳池畔的躺椅上放松休憩。Spa提供各种按摩和美容护理服务。酒</span><span style=\"color:#64451D;line-height:1.5;\">店还为年幼客人设有儿童俱乐部和游泳池。Beachcomber Trou aux Biches Resort &amp; Spa酒店可为客人安排玻璃底船</span><span style=\"color:#64451D;line-height:1.5;\">观光、深海捕鱼以及浮潜等各种活动。客人亦可享受沙滩排球和网球运动的乐趣。</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/2fca0138-e2d9-4f33-b804-08bafc67884c.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<b><img src=\"/attached/image/2016/06/20/14aa61cd-5f2c-47c5-9a1f-ada6821904c2.jpg\" alt=\"\" /></b> \r\n</p>\r\n<p>\r\n	<b><img src=\"/attached/image/2016/06/20/8ac2111c-c54f-4057-9f7b-da4dab98e146.jpg\" alt=\"\" /><br />\r\n</b> \r\n</p>\r\n<p>\r\n	<b><img src=\"/attached/image/2016/06/20/fe9213cd-41a4-4c2e-9f3d-a1fccc669c33.jpg\" alt=\"\" /><br />\r\n</b>\r\n</p>\r\n<p>\r\n	<b><img src=\"/attached/image/2016/06/20/5c6f770e-837c-4c0e-b59b-adb15997b396.jpg\" alt=\"\" /><br />\r\n</b> \r\n</p>\r\n<p>\r\n	<b><img src=\"/attached/image/2016/06/20/69fabb26-72fa-4257-88be-2ce0917a2cac.jpg\" alt=\"\" /><br />\r\n</b> \r\n</p>\r\n<p>\r\n	<b><img src=\"/atta