INSERT INTO `dive_store` VALUES ('82126cd8-9d2d-46d5-8a91-00e16823ea4c', '2016-04-27 12:51:33', '亚洲_印度尼西亚', 'uploadfiles/store/store_cOSy1465376667716.jpg', '印尼巴厘岛-蓝宝石潜水度假村', '', '', '✈ Villa AquaMarine Diving-Bali', '<p style=\"text-align:center;\">\r\n	<span style=\"line-height:36px;\"><span style=\"font-family:\'Microsoft YaHei\';line-height:1;\"><strong><span style=\"color:#333333;font-family:NSimSun;\"><img src=\"/attached/image/2016/06/08/1963dc1a-0852-45ff-99d7-08ab98fccf36.jpg\" alt=\"\" /><br />\r\n</span></strong></span></span> \r\n</p>\r\n<h1 style=\"text-align:center;\">\r\n	<span style=\"line-height:36px;\"><span style=\"font-family:\'Microsoft YaHei\';line-height:1;\"><strong><span style=\"color:#333333;font-family:NSimSun;\"><span style=\"font-family:NSimSun;color:#337FE5;\">Villa AquaMarine Diving-Bali</span><span style=\"font-family:Arial Black;\"></span></span></strong></span></span> \r\n</h1>\r\n<p>\r\n	<span style=\"line-height:36px;\"><span style=\"font-family:\'Microsoft YaHei\';line-height:1;\"><strong><span style=\"color:#333333;font-family:NSimSun;\"><br />\r\n</span></strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:36px;\"><span style=\"font-family:\'Microsoft YaHei\';line-height:1;\"><strong><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">度假村名称</span><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">：</span></strong><span style=\"font-family:NSimSun;color:#64451D;font-size:12px;\">蓝宝石潜水度假村</span></span></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">地点：</span></strong><span style=\"color:#333333;\"></span><span style=\"color:#64451D;font-family:NSimSun;font-size:12px;\"><span style=\"line-height:12px;font-family:NSimSun;font-size:12px;\">印度尼西亚 巴厘岛 Amed地区</span></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"line-height:12px;\"><b><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">开业时间</span><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">：</span></b><span style=\"color:#333333;font-family:NSimSun;\"></span><span style=\"color:#333333;font-family:NSimSun;\"></span><span style=\"font-family:NSimSun;color:#64451D;font-size:12px;\"><span style=\"color:#64451D;font-family:NSimSun;font-size:12px;\">2010年</span></span></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><b><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">星级：</span></b></b><span style=\"color:#64451D;font-family:NSimSun;font-size:12px;\">4.5</span><span style=\"color:#64451D;font-family:NSimSun;font-size:12px;\">星</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">交通信息：</span></b><span style=\"color:#64451D;font-family:NSimSun;font-size:12px;\">度假村距巴厘岛国际机场大约需要1.5小时</span> \r\n</p>\r\n<hr />\r\n<b><b><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">教授语言</span></b><span style=\"color:#999999;\"></span><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">：</span></b><span style=\"line-height:1.5;color:#64451D;\"><span style=\"font-family:NSimSun;\"><span style=\"font-family:NSimSun;font-size:12px;\">英语 中文&nbsp;</span> \r\n<hr />\r\n</span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span> \r\n<p>\r\n	<span style=\"line-height:36px;color:#999999;font-family:NSimSun;font-size:12px;\"><span style=\"line-height:1;color:#333333;font-family:NSimSun;font-size:12px;\"><strong>其它信息</strong></span></span><span style=\"line-height:36px;color:#999999;\"><span style=\"line-height:1;color:#333333;font-family:NSimSun;\"><strong></strong><span style=\"color:#64451D;font-family:NSimSun;font-size:12px;\">：</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;line-height:1.5;color:#64451D;font-family:NSimSun;\">Villa Aquamarine酒店是一幢私人滨海别墅，位于巴厘岛东部的</span><span style=\"font-size:12px;line-height:1.5;color:#64451D;font-family:NSimSun;\">Amed</span><span style=\"font-size:12px;line-height:1.5;color:#64451D;font-family:NSimSun;\">地区。该地区以其色彩鲜艳的珊瑚礁、清澈的水</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">域和景色秀丽的海滩而闻名。这幢宽敞的禁烟别墅坐落于海滨，享有</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">Lombok Strait</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">海峡的全景。别墅和露台上提供免</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">费无线网络连接。</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">Aquamarine</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">酒店的别墅设有让清新海风吹入的大窗户。别墅的布置高雅，其</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">2</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">间卧室被起居室隔开。</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">别墅内的娱乐设备包括平面卫星电视、</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">DVD</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">光碟机和</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">iPod</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">音乐基座等。客人可以在私人泳池畅游后，在别墅的阳光甲板</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">内休息。滨海休闲活动包括水肺潜水、徒手潜水和捕鱼。别墅的管家可应要求准备美味的巴厘岛家常菜。设备齐全的</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">厨房亦可供客人享用。此外，从别墅步行不远即可抵达餐馆。\r\n	<hr />\r\n	</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span><span style=\"line-height:12px;\"><img src=\"/attached/image/2016/06/08/3d5dccd1-fc8a-4e61-aafd-2963bd6edb03.jpg\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/08/786eb2dd-585f-4d2a-8c00-941982d9a133.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span><span styl