INSERT INTO `dive_store` VALUES ('9cff0bb6-513d-40c1-9cca-efe2c0d71374', '2016-06-20 13:42:33', '非洲_塞舌尔', 'uploadfiles/store/store_UpFh1466401726366.jpg', '塞舌尔-麦亚奢华水疗度假村', '', '', '✈  Maia Luxury Resort & Spa ', '<h1 class=\"item\" style=\"font-size:1.8em;color:#003580;font-family:Helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<p>\r\n		<img src=\"/attached/image/2016/06/20/aca014ef-cd78-419b-9bd4-a36b984670c7.jpg\" alt=\"\" /> \r\n	</p>\r\n</h1>\r\n<p>\r\n	<span class=\"fn\" style=\"font-size:19px;line-height:20px;\"></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-family:NSimSun;font-size:24px;color:#337FE5;\"><strong>Maia Luxury Resort &amp; Spa</strong></span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#333333;\"><br />\r\n</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#333333;\">度假村名称：</span></strong><span style=\"color:#64451D;line-height:1.5;\">麦亚奢华水疗度假村</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong>地点：</strong><span style=\"font-family:NSimSun;line-height:20px;color:#64451D;\">塞舌尔 马埃岛</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong>开业时间：</strong><span style=\"color:#64451D;\">2006年</span>\r\n</p>\r\n<hr />\r\n<p>\r\n	<strong>星级：</strong><span style=\"color:#64451D;\">5星</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong>交通信息：</strong><span style=\"font-family:NSimSun;line-height:20px;color:#64451D;\">距离塞舌尔国家机场10公里</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong>教授语言：</strong><span style=\"font-family:NSimSun;line-height:20px;color:#64451D;\">英语、中文</span> \r\n</p>\r\n<hr />\r\n<strong></strong> \r\n<p>\r\n	<strong><span style=\"color:#333333;\">其它信息：</span></strong> \r\n</p>\r\n<p>\r\n	<span class=\"fn\" style=\"font-size:19px;line-height:20px;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;color:#64451D;\">酒店位于马埃岛（Mahe），周围环绕着获奖的园景花园，提供俯瞰印度洋的空调别墅、一间美食餐厅和一个康体中</span><span style=\"line-height:1.5;color:#64451D;\">心。Maia Luxury Resort度假酒店的每套别墅均提供私人无边际游泳池、免费无线网络连接、管家服务和iPod基座，设</span><span style=\"line-height:1.5;color:#64451D;\">有小厨房、户外凉亭和阳光甲板。客人可以在海滩上享用烛光晚餐，或在自己的别墅露台上安排私人烧烤活动。</span><span style=\"line-height:1.5;color:#64451D;\">Tec&nbsp;</span><span style=\"line-height:1.5;color:#64451D;\">Tec餐厅供应美味佳肴，并可在用餐时间外灵活提供餐饮服务。管家可以安排Spa中心内的按摩理疗服务和玫</span><span style=\"line-height:1.5;color:#64451D;\">瑰花</span><span style=\"line-height:1.5;color:#64451D;\">瓣浴。还可以练瑜伽和气功，或在泰诺健健身房锻炼。其他活动包括潜水、浮潜、皮划艇和独木舟。酒店提供特</span><span style=\"line-height:1.5;color:#64451D;\">殊的儿童项目，涵盖游览、迷你spa理疗和游泳课程。Maia Luxury Resort &amp; Spa度假酒店距离维多利亚（Victoria）有</span><span style=\"line-height:1.5;color:#64451D;\">20公里。</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/74f791bb-a03e-4b18-b4d8-dce4d35f8ca9.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/f5f53afb-a914-4fa1-a2b8-3bc117cd6067.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/1a190d8d-25bd-4d60-bb36-e17e949823d1.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/5685e732-4d56-4632-b4c0-3b35afebb02f.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/ebefd01e-75bf-40ab-aaff-4c5f425f4303.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/2460f51e-8c99-4508-840d-7dcbe7bf3e44.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/3affe1e1-88b4-495c-bece-c0c73b9ab908.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/20/c727fd52-d778-4d2f-a45f-a254214d387b.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/2fcabc56-a32a-4845-98fb-38c39be7e1e5.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/d875aa41-4c58-4c89-bf72-ca72e97d603b.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/91022631-3071-4062-b29b-a0516cccd1e4.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/cbdc04e6-e6f3-495b-94f2-087da5dadc97.jpg\" alt=\"\" /> \r\n</p>', 