INSERT INTO `dive_store` VALUES ('c2719e77-8248-4306-8225-24edbe3ba23d', '2016-01-29 18:27:44', '大洋洲_斐济', 'uploadfiles/store/store_gabI1454064402488.jpg', '斐济-阿斯特罗拉珊瑚礁潜水度假村', '', '', '✈Mai Dive Astrolabe Reef Resort', '<p style=\"text-align:center;\">\r\n	<strong><span style=\"color:#333333;\"><span style=\"color:#337FE5;font-size:24px;\"><img src=\"/attached/image/2016/01/29/7e66d22f-82f1-4f4f-92e6-7b022a3bf623.jpg\" alt=\"\" /><br />\r\n</span></span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#333333;\"><span style=\"color:#337FE5;font-size:24px;\"></span><span style=\"color:#337FE5;font-size:24px;font-family:NSimSun;\"><strong>Mai Dive Astrolabe Reef Resort</strong></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">度假村名称</span></strong><span style=\"color:#333333;\"><span style=\"font-family:NSimSun;font-size:12px;\">：</span><span style=\"color:#64451D;font-family:NSimSun;font-size:12px;\">阿斯特罗拉珊瑚礁潜水度假村</span><span style=\"color:#64451D;\"></span></span> \r\n</p>\r\n<hr />\r\n<strong><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">地点：</span></strong><span style=\"color:#333333;\"></span><span style=\"color:#64451D;font-family:NSimSun;font-size:12px;\">斐济 坎达武岛</span> \r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">开业时间：</span></strong><span style=\"color:#64451D;font-family:NSimSun;font-size:12px;\">2010</span> \r\n</p>\r\n<hr />\r\n<strong><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">星级</span></strong><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">：</span><span style=\"color:#64451D;font-family:NSimSun;font-size:12px;\">4星</span><span></span> \r\n<hr />\r\n<strong><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">交通信息：</span></strong><span style=\"color:#333333;font-family:NSimSun;\"></span><span style=\"font-family:NSimSun;font-size:12px;color:#64451D;\"><span style=\"color:#64451D;\">距离</span></span><span style=\"font-family:NSimSun;line-height:1.5;color:#64451D;\">Vunisea Airport机场有90分钟的船程</span> \r\n<hr />\r\n<strong><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">教授语言</span><span style=\"font-family:NSimSun;font-size:12px;\">：</span></strong><span style=\"color:#64451D;font-family:NSimSun;font-size:12px;\">英语、中文</span><span></span> \r\n<hr />\r\n<span></span> \r\n<p>\r\n	<strong><span style=\"color:#333333;font-family:NSimSun;font-size:12px;\">其它信息：</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;\"><span style=\"color:#64451D;font-size:14px;\"> </span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;color:#64451D;font-size:12px;\">Mai Dive Astrolabe Reef Resort酒店坐落在幽静的Ono Island岛，提供全膳住宿和俯瞰大海的吊床。全膳包括餐厅</span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">供应的一日三餐。早餐为欧陆式早餐或熟食早餐，午餐和晚餐提供各种采用当地农产品制作的斐济当地和国际美食。</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">24小时酒吧供应额外收费的本地啤酒、进口葡萄酒、各种烈酒和软饮料。所有客房都设有iPod基座以及配有折叠躺椅</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">的海景私人露台。客人可租用皮艇和水上运动器材。酒店设有24小时前台，并可提供额外收费的Wi-Fi。Mai Dive&nbsp;</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">Resort酒店位于非常适合潜水的Astrolabe Reef礁，纳迪国际机场（ Nadi International Airport）距离</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">Vunisea Airport机场有50分钟航程。Vunisea Airport机场距离Mai Dive Resort酒店有90分钟的船程。&nbsp;</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">一流的设施</span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">与得天独厚的地理位置都让Mai Dive Astrolabe Reef Resort成为坎达武岛旅游的最佳留宿酒店。周到服务和一流设施会让您此行终生难忘！</span> \r\n</p>\r\n<hr />\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/01/29/f05fd868-0f56-4a6f-a84b-30360e2944b9.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-alig