INSERT INTO `dive_store` VALUES ('e270b7f1-7ac8-460b-b452-020fcee3cbe7', '2016-06-20 15:06:02', '非洲_塞舌尔', 'uploadfiles/store/store_et8c1466408011335.jpg', '塞舌尔-塞舌尔柏嘉雅博瓦隆湾度假村', '', 'ST01', '✈ Berjaya Beau Vallon Bay Resort & Casino Seychelles ', '<p style=\"text-align:center;\">\r\n	<span style=\"color:#337FE5;font-size:24px;font-family:NSimSun;\"><strong><img src=\"/attached/image/2016/06/20/c7772915-075f-4721-a239-e4ee59e8323b.jpg\" alt=\"\" /><br />\r\n</strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#337FE5;font-size:24px;font-family:NSimSun;\"><strong>Berjaya Beau Vallon Bay Resort &amp; Casino Seychelles</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;color:#64451D;\"><strong><span style=\"color:#333333;\">度假村名称</span></strong>：</span><span style=\"font-family:NSimSun;line-height:1.5;color:#64451D;\">塞舌尔柏嘉雅博瓦隆湾度假村</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#64451D;\"><strong><span style=\"color:#333333;\">地点：</span></strong>塞舌尔 马埃岛</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#64451D;\"><strong><span style=\"color:#333333;\">开业时间</span></strong>：1999年</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#64451D;\"><strong><span style=\"color:#333333;\">星级</span></strong>：4星</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#64451D;\"><strong><span style=\"color:#333333;\">交通信息</span></strong>：距离塞舌国际机场15公里</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#64451D;\"><strong><span style=\"color:#333333;\">教授语言</span></strong>：英语、中文</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#333333;\"><strong>其它信息：</strong></span><strong></strong> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;\">Berjaya Beau Vallon Bay Resort &amp; Casino度假酒店位于马埃岛（Mahe Island）西北沿海的博瓦隆湾（Beau Vallon&nbsp;</span><span style=\"line-height:1.5;color:#64451D;\">Bay），可为客人提供室外游泳池、3间餐厅、网球场和夜间娱乐活动。度假酒店的客房均享有典雅的陈设布</span><span style=\"line-height:1.5;color:#64451D;\">置，设有空调、电视、迷你吧、保险箱、电话和带吹风机的私人浴室。客人可在中餐馆、比萨饼店以及供应自助早餐</span><span style=\"line-height:1.5;color:#64451D;\">和国际风味美食主题晚餐的户外餐厅用餐。3间酒吧供应鸡尾酒和饮品。度假酒店在客人抵达时提供迎宾饮品。Berjaya&nbsp;</span><span style=\"line-height:1.5;color:#64451D;\">Beau Vallon Bay Resort &amp; Casino度假酒店设有健身中心、水上运动设施、五星级的PADI潜水中心以及<span style=\"color:#E53333;\">赌场</span>。</span><span style=\"line-height:1.5;color:#64451D;\">度假酒店提供会议室、旅游咨询台、礼品店、儿童泳池和每天前往维多利亚（Victoria）的免费班车服务。\r\n	<hr />\r\n	</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;\"><img src=\"/attached/image/2016/06/20/94447b43-8671-4e74-97e7-0879fad7cdd1.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/32f681ce-fb9a-4f3b-9f9b-0701e6d099c8.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/d1e76a2c-7afe-4042-af53-fb6024498c9e.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/5cb8742c-d5da-451d-b30f-e7f7594fdc63.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/20/c762d16e-8fe1-4b32-a808-2da356c3d26b.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/88b9322a-9746-4761-b7f4-5f5b9a718249.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/39e8d2e9-025c-4822-afcf-26f0cc6cb3b7.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/5cb641e2-c69a-471f-86d7-b55f140c026e.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/a2696789-9c3a-4a42-a139-c6b8cfd24013.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/f56539bc-ed2c-4dc5-8646-d3a353520621.jpg\" alt=\"\" /> \r\n</p>', '01768d64-6c80-4f7c-84dc-ce3b26064a1c', '0');
