INSERT INTO `dive_store` VALUES ('f85419fe-8137-4738-b295-8ce0e81a305a', '2016-02-05 11:34:21', '北美_洪都拉斯', 'uploadfiles/store/store_p6Oa1454643631916.jpg', '洪都拉斯-海葡萄种植园潜水度假村', '', '', '✈Seagrape Plantation Resort & Diveshop', '<h2 style=\"text-align:center;font-size:30px;font-family:\'Microsoft YaHei\', \'Hiragino Sans GB\';font-weight:normal;color:#333333;background-color:#FFFFFF;\">\r\n	<strong><span style=\"color:#003399;\"><img src=\"/attached/image/2016/02/05/ee67d006-5423-470a-9281-4ec1843b1f97.jpg\" alt=\"\" /></span></strong> \r\n</h2>\r\n<p>\r\n	<span style=\"line-height:36px;color:#999999;\"><span style=\"font-family:\'Microsoft YaHei\';line-height:1;\"><strong><span style=\"color:#333333;font-family:NSimSun;background-color:#FFFFFF;\"> </span></strong></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:24px;color:#337FE5;\">Seagrape Plantation Resort &amp; Diveshop</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><strong><span style=\"color:#333333;\">度假村名称</span></strong>：<span style=\"color:#64451D;\">海葡萄种植园潜水度假村</span></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;\">地点：</span></strong><span style=\"line-height:1.5;color:#64451D;\">洪都拉斯 罗阿坦</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;\">开业时间</span></strong><span style=\"line-height:1.5;\">：<span style=\"color:#64451D;\">暂无</span></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;\">星级：</span></strong><span style=\"line-height:1.5;color:#64451D;\">3.5星</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><b><span style=\"color:#333333;font-family:NSimSun;background-color:#FFFFFF;\"></span></b></b> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#333333;\">交通信息：</span></strong><span style=\"line-height:1.5;color:#64451D;\">距离Juan Manuel Galvez机场有15分钟车程</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;\">教授语言：</span></strong><span style=\"line-height:1.5;color:#64451D;\">西班牙语、英文</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;\"> </span> \r\n</p>\r\n<hr />\r\n<span></span> \r\n<p>\r\n	<strong>其它信息：</strong> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;\">这家度假酒店处于阳光灿烂的Roatan岛，建在水上，配备一个室外游泳池、免费停车场和免费无线网络。这家明亮且</span><span style=\"color:#64451D;line-height:1.5;\">色彩丰富的酒店提供空调住宿，均配备衣柜、风扇、带淋浴和卫生间的私人浴室和冰箱。部分客房还提供有线电视和</span><span style=\"color:#64451D;line-height:1.5;\">阳台或露台。该地区配备几间餐厅，提供国内和国际美食区，包括一间距酒店仅500米的阿根廷烤肉餐厅。客人可以在</span><span style=\"color:#64451D;line-height:1.5;\">深受欢迎的West Bay海滩放松，海滩距Seagrape Plantation Resort度假酒店只有6公里，或在度假酒店的游泳池放</span><span style=\"color:#64451D;line-height:1.5;\">松。酒店还可以帮助客人安排户外活动，包括潜水和自然之旅。</span> \r\n</p>\r\n<hr />\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/06/16/cbee5f18-60c8-4b54-8b88-de18f9db6687.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/06/16/7b37be96-5d60-427c-ba7e-a58413dae559.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/02/05/79bd34dc-2e2d-4578-ad09-6369849a5283.jpg\" alt=\"\" /><br />\r\n</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/06/16/13d6cf21-d727-41f9-bcb4-052cc41fb0c9.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/02/05/6276dcf5-c900-4b00-91f1-e36af73ead2f.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/02/05/795de798-5a02-42dc-a075-c39e6a284dd2.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height: