INSERT INTO `dive_store` VALUES ('f9c67cfe-849d-4a1d-9d2f-58d7262c172f', '2016-06-20 14:37:55', '非洲_塞舌尔', 'uploadfiles/store/store_Tjfw1466405269984.jpg', '塞舌尔-塞舌尔拉布里兹希尔顿度假及Spa酒店', '', '', '✈ Hilton Seychelles Labriz Resort & Spa', '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:24px;color:#337FE5;\"><strong><img src=\"/attached/image/2016/06/20/f24716f0-2268-41ea-bd40-aa1535e1e1f5.jpg\" alt=\"\" /><br />\r\n</strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:24px;color:#337FE5;\"><strong>Hilton Seychelles Labriz Resort &amp; Spa</strong></span> \r\n</p>\r\n<p>\r\n	<span><strong><span style=\"color:#333333;\"><br />\r\n</span></strong></span> \r\n</p>\r\n<p>\r\n	<span><strong><span style=\"color:#333333;\">度假村名称：</span></strong></span><span style=\"line-height:1.5;color:#64451D;\">塞舌尔拉布里兹希尔顿度假及Spa酒店</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"line-height:1.5;\"><strong><span style=\"color:#333333;\">地点：</span></strong><span style=\"color:#64451D;\">塞舌尔&nbsp;</span></span><span style=\"line-height:1.5;color:#64451D;\">卢埃特岛</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;\">开业时间：</span></strong><span style=\"line-height:1.5;color:#64451D;\">2006年</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;\">星级：</span></strong><span style=\"line-height:1.5;color:#64451D;\">5星</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;\">交通信息：</span></strong><span style=\"line-height:1.5;color:#64451D;\">酒店距离码头有20分钟车程，从那里乘船35分钟便可抵达塞舌尔国际机场</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;\">教授语言</span></strong><span style=\"line-height:1.5;\">：</span><span style=\"line-height:1.5;color:#64451D;\">英语、中文</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong>其它信息：</strong> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;color:#64451D;\">酒店位于卢埃特岛（Silhouette Island），提供带私人露台且设备齐全的宽敞别墅，拥有一个提供全方位服务的Spa中</span><span style=\"line-height:1.5;color:#64451D;\">心和位于热带棕榈树之间的无边游泳池。每栋别墅均设有豪华寝具、平面电视、DVD播放机和沙发。部分客房设有印</span><span style=\"line-height:1.5;color:#64451D;\">度洋海景。Hilton Seychelles酒店设有7间餐厅和咖啡馆，在优雅的氛围里为您提供意大利式、日式、法式以及当地特</span><span style=\"line-height:1.5;color:#64451D;\">色的美味菜肴。Bar Lo Brizan酒吧提供现场娱乐节目以及热带鸡尾酒。入住Hilton Seychelles酒店期间，您可以享用5星</span><span style=\"line-height:1.5;color:#64451D;\">级的PADI潜水中心，前往游览岛上丰富多彩的珊瑚礁。酒店位于植被丰富的岩石地带，附近有众多的步行和远足路</span><span style=\"line-height:1.5;color:#64451D;\">径。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;color:#64451D;\"> </span> \r\n</p>\r\n<hr />\r\n<div style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/20/11525d03-198f-419e-a19d-5f382a7d328c.jpg\" alt=\"\" /> \r\n</div>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/204f0b80-f8cd-4b18-8947-dc3bf5e6b4f3.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/20/b8ec474d-8a80-49b6-adb2-01f5a6437046.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/20/d6219682-9261-4aa7-8cd5-e9e6b23ccd0e.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/20/ab7594d8-3d9b-4fd6-bc30-a7ef8de6408e.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/6a936b81-96de-49f9-a861-ca1e38cae232.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/b8afe0d3-91e4-42cc-9673-4b2cef13ad21.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/07c0c8bd-2004-42e9-aee9-935b08f431ac.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/77376b8b-e13e-4607-8bed-564220186557.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/4122a53e-219f-4318-ac02-0979c9203cea.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/ed2215ba-7151-40b3-9091-07fcd8871eff.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/20/6ec480c7-6c9c-4897-923b-aafc56a41a