INSERT INTO `dive_store` VALUES ('fbc9525e-5480-4c1a-8fa2-7527b5fb2456', '2016-06-01 16:08:38', '亚洲_马尔代夫', 'uploadfiles/store/store_3ARS1465979174791.jpg', '马尔代夫-菲诺度假村', '', '', '✈ Finolhu', '<p style=\"text-align:center;\">\r\n	<span style=\"color:#337FE5;font-size:24px;line-height:1.5;\"><img src=\"/attached/image/2016/06/15/777c7416-5922-4018-a22a-e455ff22c3da.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#337FE5;font-size:24px;line-height:1.5;font-family:NSimSun;\"><strong>Finolhu</strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#000000;font-size:12px;line-height:1.5;\"><strong>度假村名称：</strong><span style=\"color:#64451D;\">菲诺度假村</span></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#000000;font-size:12px;line-height:1.5;\"> </span> \r\n</p>\r\n<hr />\r\n<strong>地点：</strong><span style=\"color:#64451D;\">马尔代夫群岛 芭环礁</span> \r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#000000;font-size:12px;line-height:1.5;\"> </span> \r\n</p>\r\n<hr />\r\n<strong>开业时间：</strong><span style=\"color:#64451D;\">2016年6月1日</span> \r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#000000;font-size:12px;line-height:1.5;\"> </span> \r\n</p>\r\n<hr />\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#000000;font-size:12px;line-height:1.5;\"> </span> \r\n</p>\r\n<p>\r\n	<b>星级：</b><span style=\"color:#64451D;\">7</span><span style=\"color:#64451D;\">星</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;\"> </span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b>交通信息：</b><span style=\"font-size:12px;line-height:1.5;color:#64451D;\">度假村距机场有30分钟船程</span>\r\n</p>\r\n<hr />\r\n<strong>教授语言：</strong><span style=\"color:#64451D;\">英语、中文</span> \r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#000000;font-size:12px;line-height:1.5;\"> </span> \r\n</p>\r\n<hr />\r\n<strong>其它信息：</strong> \r\n<p>\r\n	<span style=\"line-height:1.5;font-family:微软雅黑;color:#64451D;\">全岛将原始淳朴的节能建筑完美融入周围原自然奢华之地、蕴含着当地的自然风情。菲诺岛将现代奢华与马尔代夫的自然魅力完美结合、52栋奢华海景别墅、私人泳池、露台和沙滩、无与伦比的建筑风格设计、贴身管家提供最具个性化的服务、最适合寻求精致奢华体验的情侣们度过一段珍贵的时光。</span><span style=\"color:#64451D;line-height:1.5;\">强烈的设计感，充满了音乐和美食的隐居处，</span><span style=\"color:#64451D;line-height:1.5;\">Finolhu将在2016年下半年全新开业。</span><span style=\"color:#64451D;line-height:1.5;\">最好的DJ唤醒你的耳朵，</span><span style=\"color:#64451D;line-height:1.5;\">细腻的沙滩刺激你的感官。</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;line-height:1.5;\">耳目一新，不可思议，与你相约菲诺！</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/06/01/3ef935bf-597d-42c3-a3cd-90b21a3ae1f1.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/06/15/e49fc413-302c-4a17-bcf1-a6d92562152a.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/06/01/f0c66b15-d52d-46a2-bdaf-858512fc841a.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;\"><img src=\"/attached/image/2016/06/15/ba1116dd-7ec0-4736-82d2-19af3097cbe4.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;\"><img src=\"/attached/image/2016/06/15/24698740-935c-48ea-8d84-ee86065cec28.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#64451D;\"><img src=\"/attached/image/2016/06/01/4886cc25-4745-44fb-8db8-bde2c4aa1e9b.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#64451D;\"><img src=\"/attached/image/2016/06/01/c924567a-5b30-48b8-812b-73f5bed294ea.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p styl