INSERT INTO `dive_store` VALUES ('fe5af1af-5fd5-4f7d-835f-7dd8112bb69d', '2016-06-24 10:46:56', '南美_厄瓜多尔', 'uploadfiles/store/store_6dxs1466737264632.jpg', '厄瓜多尔-加拉帕戈斯套房酒店', '', '', '✈ Hotel Galapagos Suites', '<p style=\"text-align:center;\">\r\n	<span style=\"font-family:NSimSun;font-size:24px;color:#337FE5;\"><strong><img src=\"/attached/image/2016/06/24/343467d5-e92e-4e2d-a295-db2372465878.jpg\" alt=\"\" /><br />\r\n</strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-family:NSimSun;font-size:24px;color:#337FE5;\"><strong>Hotel Galapagos Suites</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;color:#333333;\"><strong>度假村名称：</strong></span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">加拉帕戈斯套房酒店</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#333333;\"><strong>地点：</strong></span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">厄瓜多尔 阿约拉港</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#333333;\"><strong>开业时间</strong></span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">：暂无</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;\">星级</span></strong><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">：4星</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#333333;\"><strong>交通信息</strong></span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">：暂无</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;\">教授语言：</span></strong><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">英语、中文</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong>其它信息：</strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;color:#64451D;\">酒店位于加拉帕戈斯群岛，坐落在阿约拉港（Puerto Ayora），距离海滨有5分钟的步行路程，提供带私人阳台和吊床</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">的客房。客房设有空调和带有线频道的液晶电视，均享有带亮丽织品的简约风格装饰，并设有保险箱、私人浴室和带</span><span style=\"line-height:1.5;color:#64451D;font-family:NSimSun;\">吊床的私人阳台或天井。酒店每天早晨供应美式早餐，您也可以在俯瞰花园的露台上享用。酒店距离鱼市场有200米，</span><span style=\"color:#64451D;font-family:NSimSun;line-height:1.5;\">距离达尔文研究站（Darwin Research Station）有500米。</span> \r\n</p>\r\n<hr />\r\n<div style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/59267e2b-e8a8-47d0-b2a3-b6bdf7ec9e2e.jpg\" alt=\"\" /><br />\r\n</div>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/24/2fe0d6e5-24fe-44c0-b235-7184fe237075.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/bd035940-e222-4e94-b88d-801ffb0ae61c.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/0f49899b-a0ce-4767-850c-35f7a4412634.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/aa1ce03b-d0ed-4534-9ba4-ff9460bf01a6.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/d0588ee9-20a9-4286-8ff3-cb1927549668.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/425ac7ad-2055-48d0-a63a-85f9e57071a4.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/131c09ee-d827-4657-be9a-e8c775ae79c0.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/24/621e9658-11d3-4d48-b153-cb0297514e1b.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/24/6c474d75-bf76-4f54-b6e4-1ef850854539.jpg\" alt=\"\" /> \r\n</p>', '01768d64-6c80-4f7c-84dc-ce3b26064a1c', '0');
