INSERT INTO `dive_travel` VALUES ('16eb3a49-06a0-4f77-80ef-f8b406dd3029', '2016-05-11 16:22:57', '<h1 style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/05/11/7b219ac8-7a04-4731-aa3e-0cf532df8da8.jpg\" alt=\"\" /><br />\r\n</h1>\r\n<h1 style=\"text-align:center;\">\r\n	SY Philipine Siren<br />\r\n</h1>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;color:#337FE5;\"><b>菲律宾塞壬号</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;\"><b><br />\r\n</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船名：</span></b><span style=\"color:#333333;\"><span style=\"color:#64451D;\">菲律宾塞壬号</span><span style=\"color:#64451D;\"></span></span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船体：</span></b><span style=\"color:#64451D;\">双层</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">目的地</span></b><b><strong><span style=\"color:#64451D;\">：</span></strong></b><span style=\"color:#64451D;\">菲律宾图巴塔哈群岛，科隆，阿波礁</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">舱位数量</span></b><b><span style=\"color:#333333;\">：</span></b><span style=\"color:#333333;\"></span><span style=\"color:#64451D;\">8</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">可容纳客人</span></b><span style=\"color:#333333;\">:</span><span style=\"color:#64451D;\">&nbsp;16</span><span style=\"color:#64451D;line-height:1.5;font-family:NSimSun;\">位</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">其它信息</span></b><b><span style=\"color:#333333;\">:</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"> </span></b> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;line-height:1.5;\">游轮Philippine Siren是一艘40米长，专为现代潜水员设计的豪华木制机帆船游轮。它迷人大气的外观，保证你在菲律宾海域找不到第二艘这样完美的邮轮。游轮上不仅拥有宽敞舒适且环境一流的室外用餐区供你放松，享用美味餐食。更让你惊喜的还有它超舒适，超特别的潜水甲板。</span><span style=\"color:#64451D;line-height:1.5;\">船上共配有8间客舱，可同时接纳16位游客出海巡航。而且在8间带有空调系统的客舱内，均配有均配有私人影音设备，私人卫浴室，独立空调系统，洗浴用品，吹风机，毛巾，储物柜等... 保证你的游轮潜水之旅是充满了惊喜和舒适的。其中有2间客舱拥有可调整床铺（可以根据客人需要，调整成一张双人床或两张单人床)，2间客舱为三床客舱，在包船团队出行时，可以满足游客的不同需求。</span> \r\n</p>\r\n<hr />\r\n<img src=\"/attached/image/2016/05/11/6ba34806-ffcf-44fe-9815-da362831404f.jpg\" alt=\"\" />\r\n<p>\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/05/11/81f7b712-6632-4a22-93cd-92f9392adf5a.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/05/11/6ac79135-9625-4118-b141-138fafc51a76.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/05/11/6b484484-e39d-4a8a-bbb2-0ed76926cc56.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/05/11/97adb86f-10cd-4b40-b507-a624d82ebae0.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/05/11/85f4a97e-dbe2-40ea-a563-2b7f9f263211.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/05/11/3b533936-3f27-4370-9be7-462eb8911f22.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;line-height:1.5;\"><img src=\"/attached/image/2016/05/11/5794e712-bc8a-4a38-bfcc-550ef97e9d7f.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>', null, 'uploadfiles/travel/travel_gn0p1462955682041.jpg', '菲律宾塞壬号', null, '', '| SY Philipine Siren', '01768d64-6c80-4f7c-84dc-ce3b26064a1c', '菲律宾图巴塔哈群岛、薄荷岛/米沙鄢群岛', '', '菲律宾', '', 'SY Philipine Siren', '0');
