INSERT INTO `dive_travel` VALUES ('217a9dc5-5ba3-491c-9046-d8cbe3a5f262', '2016-05-10 14:07:30', '<h1 style=\"text-align:center;\">\r\n	<span style=\"line-height:1;color:#337FE5;\"><img src=\"/attached/image/2016/05/10/1d3df5da-74cf-4d88-939c-4cd8fda6cc47.jpg\" alt=\"\" /><br />\r\n</span> \r\n</h1>\r\n<h1 style=\"text-align:center;\">\r\n	<span style=\"line-height:1;color:#000000;\">Belize Aggressor II</span><br />\r\n</h1>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;color:#337FE5;\"><b>伯利兹侵略者II</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;\"><b><br />\r\n</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船名：</span></b><span style=\"color:#333333;\"></span><span style=\"color:#64451D;\">伯利兹侵略者II</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船体：</span></b><span style=\"color:#64451D;\">双层</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">目的地</span></b><b><strong><span style=\"color:#64451D;\">：</span></strong></b><span style=\"color:#64451D;\">伯利兹</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">舱位数量</span></b><b><span style=\"color:#333333;\">：</span></b><span style=\"color:#333333;\"></span><span>10</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">可容纳客人</span></b><span style=\"color:#333333;\">:</span><span style=\"color:#64451D;\">&nbsp;20</span><span style=\"color:#64451D;line-height:1.5;font-family:NSimSun;\">位</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">其它信息</span></b><b><span style=\"color:#333333;\">:</span></b> \r\n</p>\r\n<p style=\"color:#555555;font-family:\'Lucida Sans\', \'Lucida Grande\', \'Trebuchet MS\', Tahoma, Arial, \'Helvetica Neue\', Helvetica, Geneva, Lucida, sans-serif;font-size:13.76px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#64451D;\">游轮Belize Aggressor III是一艘33米长的豪华潜水游轮，具有很高安全性和稳定性，高标准的动力和舒适度，它使用柴油发力，巡航速度在15海里/小时左右。它每周六都组织一场由伯利兹市出发，为期1周的游轮潜水之旅。最多可共18位潜水员一同出海！</span><span style=\"color:#64451D;font-size:13.76px;line-height:1.5;\">船上共有9间豪华双人客舱：7间双人床+单人床（上下铺模式)客舱，1间豪华主客舱（双人大床客舱)，1间双床客舱（上下铺模式)。所有客舱都配有私人卫浴室，空调，电视和DVD播放器等贴心设施。此外，游轮Belize Aggressor II还拥有一个美丽的沙龙室，一个15平方米带有部分遮阳板的阳光甲板（配有躺垫和躺椅)，酒吧，热水桑拿池，烧烤，数码照片工作中心，滑板车，和皮划艇供旅客使用。</span> \r\n</p>\r\n<hr />\r\n<b><img src=\"/attached/image/2016/05/10/5c8f0ec4-2e4b-414d-bbbb-505af79b6955.jpg\" alt=\"\" /></b> \r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/10/d053ce2b-7fd7-45c8-9fdf-d2fdf48749d2.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/10/f14410dd-9ded-4d5c-b49c-4c38a3be61f5.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/10/67202d71-775b-4854-a769-e810230c5a5c.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/10/d5f34330-dbc9-47d6-8733-4781382ed9bb.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/10/7a0f4c30-f104-4885-a93e-665ffe67f0a0.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/10/64fe8b53-476c-4a16-a866-d5a66da8cc20.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/10/d2554ea2-dc2e-4782-b6a0-f940f5cc6d76.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/10/69a1745c-95c9-4d70-9415-41c118499383.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>', null, 'uploadfiles/travel/travel_llwz1462860449686.jpg', '伯利兹侵略者II', null, '', '| Belize Aggressor III', '01768d64-6c80-4f7c-84dc-ce3b26064a1c', '伯利兹', '', '伯利兹', '', 'Belize Aggressor II', '0');
INSERT INTO `dive_travel` VALUES ('218d60cb-97a9-4a91-baf6-21fbd80d6bcb', '2016-05-18 09:37:53', '<h1 style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/05/18/2bb56cba-8944-4079-80d2-342ec2bd51fe.jpg\" alt=\"\" /><br />\r\n</h1>\r\n<h1 style=\"text-align:center;\">\r\n	Mosaique<br />\r\n</h1>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;color:#337FE5;\"><b>默塞克</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;\"><b><br />\r\n</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船名：</span></b><span style=\"color:#64451D;\">默塞克</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船体：</span></b><span style=\"color:#64451D;\">双层</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">目的地</span></b><span style=\"color:#64451D;\">：马尔代夫中部环礁</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><span style=\"color:#333333;\">舱位数量</span></b><b><span style=\"color:#333333;\">：</span></b><span style=\"color:#333333;\"></span><span>11</span> \r\n</p>\r\n<span style=\"color:#333333;\"><span style=\"color:#64451D;\"> \r\n<hr />\r\n</span></span><span></span><span></span><span></span><span></span> \r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">可容纳客人</span></b><span style=\"color:#333333;\">:</span><span style=\"color:#64451D;\">&nbsp;22</span><span style=\"color:#64451D;line-height:1.5;font-family:NSimSun;\">位</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">其它信息</span></b><b><span style=\"color:#333333;\">:</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"color:#64451D;line-height:1.5;\">超豪华船宿潜水船 Mosaique 是马尔代夫唯一一艘在房间内配备 360 度景观和私人阳台的船宿潜水船，其中一个设计目标是将室内和室外生活区域很好的结合起来。主甲板以及房间内的景观窗户让客人不会错过任何沿途环礁和泻湖的美景。同时，你可以享受在私人阳台上迎着海风冲淋的美妙感觉，也可以与朋友们一起分享上层甲板上可供8人同时使用的超大按摩池，或在120平米日光浴甲板上沐浴阳光。其他特色服务包括：潜水中心，健康俱乐部和水疗中心，健身房，桑拿浴室。船上专业的潜水导游、厨师以及其它服务人员一定会让你的整个潜水旅途满意而归。</span><span style=\"color:#64451D;line-height:1.5;\"></span> \r\n</p>\r\n<hr />\r\n<b><img src=\"/attached/image/2016/05/18/5a9a9b5e-ddd2-4f5b-8782-6cf1b7c43d5a.jpg\" alt=\"\" /></b> \r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/18/a934c88a-ff04-4e08-9d68-14e3c6a2b89c.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/18/51685feb-b9f0-4f9a-8be5-506459297218.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/18/0f489087-70ef-4448-b0dd-74e2baa1a5b9.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/18/cbf0b510-bfdd-49fb-949c-674ecf1d6b71.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/18/a802484f-19ab-413a-8331-9b4eee2e8940.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/18/ead3eb3c-9957-4b52-95fa-b6ab86a17de8.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/05/