INSERT INTO `dive_travel` VALUES ('78a25988-8077-4e22-a6c9-771f010afcac', '2016-05-11 10:17:55', '<h1 style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/05/11/68a2f999-487c-4084-ae39-c6eb77d49039.jpg\" alt=\"\" /><br />\r\n</h1>\r\n<h1 style=\"text-align:center;\">\r\n	Maldives Aggressor<br />\r\n</h1>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;color:#337FE5;\"><b>马尔代夫侵略者号</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;\"><b><br />\r\n</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船名：</span></b><span style=\"color:#333333;\"></span><span style=\"color:#64451D;\">马尔代夫侵略者号</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船体：</span></b><span style=\"color:#64451D;\">双层</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">目的地</span></b><b><strong><span style=\"color:#64451D;\">：</span></strong></b><span style=\"color:#64451D;\">马尔代夫</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">舱位数量</span></b><b><span style=\"color:#333333;\">：</span></b><span style=\"color:#333333;\"></span><span><span style=\"color:#64451D;\">10</span></span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">可容纳客人</span></b><span style=\"color:#333333;\">:</span><span style=\"color:#64451D;\">&nbsp;20</span><span style=\"color:#64451D;line-height:1.5;font-family:NSimSun;\">位</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">其它信息</span></b><b><span style=\"color:#333333;\">:</span></b> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;\">游轮Maldives Aggressor 是一艘长34米，宽7米的豪华游轮，建于2009年。船上共有10间客舱，可接纳20位旅客同时出行。船上拥有一间舒适宽敞的沙龙室，一个阳光甲板平台（部分区域带有遮阳板）带有躺椅，一个全方位服务吧台，一个相机工作中心。游轮Maldives Aggressor船上共有10间客舱：6间豪华客舱（上下床－上床为单人铺，下床为双人铺），2间双床客舱，和2间经济双床客舱。所有客舱都配有私人卫浴室，空调，电视，DVD播放机，和吹风等设施。</span> \r\n</p>\r\n<hr />\r\n<img src=\"/attached/image/2016/05/11/3d9bb4cd-ddf1-4c6c-8de6-0508dc595147.jpg\" alt=\"\" /> \r\n<p style=\"color:#555555;font-family:\'Lucida Sans\', \'Lucida Grande\', \'Trebuchet MS\', Tahoma, Arial, \'Helvetica Neue\', Helvetica, Geneva, Lucida, sans-serif;font-size:13.76px;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:12px;color:#64451D;\"><img src=\"/attached/image/2016/05/11/2be50dac-1a72-44f9-b2b8-2ffa35b1d55b.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"color:#555555;font-family:\'Lucida Sans\', \'Lucida Grande\', \'Trebuchet MS\', Tahoma, Arial, \'Helvetica Neue\', Helvetica, Geneva, Lucida, sans-serif;font-size:13.76px;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:12px;color:#64451D;\"><img src=\"/attached/image/2016/05/11/4f72ac78-0240-4528-b410-c7a013c688f8.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"color:#555555;font-family:\'Lucida Sans\', \'Lucida Grande\', \'Trebuchet MS\', Tahoma, Arial, \'Helvetica Neue\', Helvetica, Geneva, Lucida, sans-serif;font-size:13.76px;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:12px;color:#64451D;\"><img src=\"/attached/image/2016/05/11/1928b931-c7e0-41c9-aa52-08a25f2a1946.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"color:#555555;font-family:\'Lucida Sans\', \'Lucida Grande\', \'Trebuchet MS\', Tahoma, Arial, \'Helvetica Neue