INSERT INTO `dive_travel` VALUES ('be217618-54ed-45a7-8eab-70c1362687e2', '2016-05-10 15:58:57', '<h1 style=\"text-align:center;\">\r\n	<span style=\"line-height:1;\"><img src=\"/attached/image/2016/05/10/57d7ff5e-d6e7-4080-8bf6-0b53738d0f20.jpg\" alt=\"\" /><br />\r\n</span> \r\n</h1>\r\n<h1 style=\"text-align:center;\">\r\n	<span style=\"line-height:1;\">Island Dancer</span><br />\r\n</h1>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;color:#337FE5;\"><b>岛舞者号</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;\"><b><br />\r\n</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船名：</span></b><span style=\"color:#333333;\"></span><span style=\"color:#64451D;\">岛舞者号</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船体：</span></b><span style=\"color:#64451D;\">双层</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">目的地</span></b><b><strong><span style=\"color:#64451D;\">：</span></strong></b><span style=\"color:#64451D;\">斐济</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">舱位数量</span></b><b><span style=\"color:#333333;\">：</span></b><span style=\"color:#333333;\"><span style=\"color:#64451D;\">5</span></span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">可容纳客人</span></b><span style=\"color:#333333;\">:</span><span style=\"color:#64451D;\">&nbsp;10</span><span style=\"color:#64451D;line-height:1.5;font-family:NSimSun;\">位</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">其它信息</span></b><b><span style=\"color:#333333;\">:</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"> </span></b> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;line-height:1.5;\">游轮Island Dancer 号，被当地人称作“Sere Ni Wai”, 是一艘美丽又结实的潜水游轮。它每周六由斐济首都苏瓦(Suva)出发，前往斐济群岛及周边潜点进行为期一周的游轮潜水之旅。船上共有5间客舱，共可接纳10位游客，特别适合小型潜水团体到斐济岛探险游玩儿搭乘！</span><span style=\"color:#64451D;line-height:1.5;\">船上所有舱室内都配有空调系统，漂亮的沙龙室可以让所有潜水员都充分享受到使用它的娱乐系统观看电影，查阅照片的乐趣。游轮上舒适闲散的氛围，让你在潜水之余可以和朋友们一起放松，阅读，晒太阳，甚至打牌，钓鱼。总之，船上星级服务标准一定不会让任何一位旅客感到无聊。</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<img src=\"/attached/image/2016/05/10/d647e7ee-cf10-496c-ac0b-c2166e4046c5.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/10/3692b3a1-5653-4d99-8801-53110291aad2.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/10/5fda26bc-e895-41d8-b0fe-2a7e8317660a.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/10/f8765143-e9db-4b31-865e-c71118bb14d9.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/10/63e32b71-6eba-4434-918a-a45fe01e37cd.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/10/0208011f-6fb5-4d9d-9200-66c4064ee186.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/10/d23cbcc7-6e79-4fae-9f98-35d4a680e093.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/10/a39b1ffe-4c0c-4a78-9b3e-01283fa5d841.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/10/204c86ef-d086-4617-a4d5-38f6ff7f8f60.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/10/1c45ffe7-f99d-40f3-be0f-43d20bfc7c81.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/10/1e57aeb0-c1f5-401b-9a50-6c535ca36307.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, 'uploadfiles/travel/travel_RsMx1462867579189.jpg', '岛舞者号', null, '', '| Island Dancer', '01768d64-6c80-4f7c-84dc-ce3b26064a1c', '苏瓦', '', '斐济', '', 'Island Dancer', '0');
INSERT INTO `dive_travel` VALUES ('bfd39fa9-e153-4da5-bfc7-1016b6936b70', '2016-05-11 15:36:58', '<h1 style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/05/11/32a5c952-142a-4626-903b-26546e501a28.jpg\" alt=\"\" /><br />\r\n</h1>\r\n<h1 style=\"text-align:center;\">\r\n	SY Indo Siren<br />\r\n</h1>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;color:#337FE5;\"><b>印度塞壬号</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;\"><b><br />\r\n</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船名：</span></b><span style=\"color:#333333;\"><span style=\"color:#64451D;\">印度塞壬号</span><span style=\"color:#64451D;\"></span></span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船体：</span></b><span style=\"color:#64451D;\">双层</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">目的地</span></b><b><strong><span style=\"color:#64451D;\">：</span></strong></b><span style=\"color:#64451D;\">四王岛，科莫多群岛</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">舱位数量</span></b><b><span style=\"color:#333333;\">：</span></b><span style=\"color:#333333;\"></span><span style=\"color:#64451D;\">8</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">可容纳客人</span></b><span style=\"color:#333333;\">:</span><span style=\"color:#64451D;\">&nbsp;16</span><span style=\"color:#64451D;line-height:1.5;font-family:NSimSun;\">位</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">其它信息</span></b><b><span style=\"color:#333333;\">:</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"> </span></b> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;\">游轮Indo Siren是一艘专为现代潜水员设计的五星级豪华享受型的传统印尼“gaft-rigged Phinisi\"木制机帆船游轮。是常年巡航于科莫多国家公园，四王群岛和班达海。长40米，从2011年4月第一次出海至今，获得的好评不断，几乎所有和Indo Siren一起出游过的潜水员都对这艘豪华游轮赞不绝口！游轮上不仅拥有宽敞舒适的室外休闲区供你放松，享用美味餐食。更让你惊喜的还有它超舒适，超特别的潜水甲板－配有专业的相机工作室，可以让船上所有的水下摄影师/摄像师有足够的空间准备，修改照片，为装备充电。</span><span style=\"color:#64451D;\">Indo Siren为保证船上旅客的舒适度，每次旅程只接纳最多16位潜水员出行，船上有8间客舱可供旅客住宿使用。保证你的游轮潜水之旅是充满了惊喜和舒适的!\r\n	<hr />\r\n	</span>\r\n</p>\r\n<p>\r\n	<b><span style=\"color:#333333;\"><span style=\"color:#64451D;\"><img src=\"/attached/image/2016/05/11/60c8267a-2e4d-4889-8a41-0d8c7e360769.jpg\" alt=\"\" /></span></span></b> \r\n</p>\r\n<p>\r\n	<b><span style=\"color:#333333;\"><span style=\"color:#64451D;\"><img src=\"/attached/image/2016/05/11/2f175e87-2488-405a-9d33-c8e6a927516d.jpg\" alt=\"\" /><br />\r\n</span></span></b> \r\n</p>\r\n<p>\r\n	<b><span style=\"color:#333333;\"><span style=\"color:#64451D;\"><img src=\"/attached/image/2016/05/11/76d9c9b6-c0bc-4814-b741-0eecb6a9f669.jpg\" alt=\"\" /><br />\r\n</span></span></b> \r\n</p>\r\n<p>\r\n	<b><span style=\"color:#333333;\"><span style=\"color:#64451D;\"><img src=\"/attached/image/2016/05/11/36f6163d-301c-470a-9366-2511205d3025.jpg\" alt=\"\" /><br />\r\n</span></span></b> \r\n</p>\r\n<p>\r\n	<b><span style=\"color:#333333;\"><span style=\"color:#64451D;\"><img src=\"/attached/image/2016/05/11/fef64984-6a36-4ccb-a095-39aa11026698.jpg\" alt=\"\" /><br />\r\n</span></span></b> \r\n</p>\r\n<p>\r\n	<b><span style=\"color:#333333;\"><span style=\"color:#64451D;\"><img src=\"/