INSERT INTO `dive_travel` VALUES ('dfe6c2ed-dd0d-4df6-9cef-c1202cc71f4e', '2015-10-25 01:05:59', '<p style=\"text-align:center;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<img src=\"/attached/image/2016/01/20/a5e97bdf-5d5b-4839-b7e6-058f8dca9130.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:24px;font-family:\'Arial Black\';color:#000000;\"><strong>Scubaspa Ying &amp; Yang</strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"color:#337FE5;font-family:\'Arial Black\';font-size:16px;\">莹Spa姐妹号</span></strong> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<strong><span style=\"font-size:12px;\">船名：</span></strong><span style=\"font-size:12px;\"></span><span style=\"color:#64451D;font-family:SimHei;font-size:14px;\">莹Spa姐妹号</span><span style=\"font-size:12px;color:#64451D;font-family:SimHei;\"></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><span style=\"font-size:12px;\">船体：</span><span style=\"color:#64451D;font-size:12px;\">3</span></b><span style=\"color:#64451D;font-size:12px;\">层</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;\"></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';line-height:1.5;font-size:12px;\"><strong>目的地：</strong></span><span style=\"font-family:\'Microsoft YaHei\';line-height:1.5;color:#64451D;font-size:12px;\">马尔代夫群岛</span> \r\n</p>\r\n<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';\"> \r\n<hr />\r\n</span><span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';\"><span style=\"line-height:1.5;font-size:12px;\"><strong>舱位数量：</strong></span><span style=\"color:#64451D;line-height:1.5;font-size:14px;\">12</span></span><span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';\"> \r\n<hr />\r\n<span style=\"line-height:1.5;font-size:12px;\"><strong>可容纳人数：</strong></span><span style=\"color:#64451D;line-height:1.5;font-size:14px;\">24位</span>\r\n<hr />\r\n<span style=\"line-height:1.5;font-size:14px;\"><strong>其它信息：</strong></span></span> \r\n<p>\r\n	<span style=\"font-size:14px;color:#64451D;background-color:#FFFFFF;\"><span style=\"background-color:#FFFFFF;\">Scubaspa Ying\\Yang是俩艘崭新的姐妹潜水船宿，长50米，宽11.2米。该游轮的非凡之处在于它为潜水爱好者，情侣夫妻，朋友玩伴们提供了一种全新的潜水假期概念。</span><span style=\"background-color:#FFFFFF;\">该游轮提供多种不同类型的船宿套餐可供选择，以便完全满足各类旅客对马尔代夫这个世外桃源的憧憬之情，让旅客在享受和探索间度过一个愉快的船宿假期。无论潜水与否，这艘奢华的游轮都值得体验。</span></span><span style=\"font-size:14px;color:#64451D;background-color:#FFFFFF;\">船上的每一处细节都是充满了人性化和美感的，</span><span style=\"font-size:14px;color:#64451D;background-color:#FFFFFF;\">私密隐私的区域可以看到大海的全景，加上特别设计的露天日光浴台，</span><span style=\"font-size:14px;color:#64451D;background-color:#FFFFFF;\">船上的餐厅一直以来都为旅客提供着当地最棒的传统菜肴结合国际菜肴的美食风格。船上的厨师会为旅客准备多样化的菜肴，并且随餐提供最棒的美酒供旅客品尝。此外，该游轮船上还有2个酒水吧台可以全天为旅客供应鸡尾酒或果汁。</span><span style=\"font-size:14px;color:#64451D;background-color:#FFFFFF;\">所有客舱内都配有一张大型的双人床，部分客舱甚至还配有一张额外的单人床。客舱内的设施包括：独立的空调系统，床下宽敞的行李箱置放空间，衣柜等客舱设施，并且配有一间设计感十足的私人卫浴室。\r\n	<hr />\r\n	</span>\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<img src=\"/attached/image/2016/01/20/4bbced66-d0ec-44db-add2-d7efb587b874.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<img src=\"/attached/image/2016/01/20/9a617cfb-b4fc-4b61-81c6-34ea58a85352.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<img src=\"/attached/image/2016/01/20/50ccdf80-71d7-4d19-9154-ffab5f383e20.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<img src=\"/attached/image/2016/01/20/aca63295-6b46-41f3-9eed-c472f18ce501.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<img src=\"/attached/image/2016/01/20/baa865e0-5640-42a2-af69-d23ac132e378.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<img src=\"/attached/image/2016/01/20/b3b1c09c-f045-41c8-8406-fef59e7149f9.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/01/20/cf435076-3bce-4df1-934e-860fe5fb43ab.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/01/20/6c352cff-15ef-4dda-92d9-ceb25d8c147a.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/01/20/432d4afc-1858-4477-a349-b1a305549148.jpg\" alt=\"\" /> \r\n</p>', null, 'uploadfiles/travel/travel_WgUP1453302154741.jpg', '莹Spa姐妹号', '15000', 'ST01', '|  Scubaspa Yang&Ying ', '6a040c09-5758-41f8-8910-bf556692e824', '马尔代夫', 'Male', '马尔代夫', 'Maldives', 'Scubaspa Ying & Yang', '5');
