INSERT INTO `dive_travel` VALUES ('fca1edad-fb7d-436e-be5e-6bfba25449df', '2016-05-11 09:28:26', '<h1 style=\"text-align:center;\">\r\n	<span style=\"line-height:1;\"><img src=\"/attached/image/2016/05/11/3cd88175-f8a9-4d0e-83d5-e585d611bd24.jpg\" alt=\"\" /><br />\r\n</span> \r\n</h1>\r\n<h1 style=\"text-align:center;\">\r\n	<span style=\"line-height:1;\">Komodo Dancer</span><br />\r\n</h1>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;color:#337FE5;\"><b>科莫多舞者</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:24px;\"><b><br />\r\n</b></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船名：</span></b><span style=\"color:#333333;\"></span><span style=\"color:#64451D;\">科莫多舞者</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船体：</span></b><span style=\"color:#64451D;\">双层</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">目的地</span></b><b><strong><span style=\"color:#64451D;\">：</span></strong></b><span style=\"color:#64451D;\">图兰奔，松巴哇岛东部</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">舱位数量</span></b><b><span style=\"color:#333333;\">：</span></b><span style=\"color:#333333;\"></span><span style=\"color:#64451D;\">8</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">可容纳客人</span></b><span style=\"color:#333333;\">:</span><span style=\"color:#64451D;\">&nbsp;16</span><span style=\"color:#64451D;line-height:1.5;font-family:NSimSun;\">位</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">其它信息</span></b><b><span style=\"color:#333333;\">:</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"color:#64451D;line-height:1.5;\"><span style=\"color:#64451D;line-height:1.5;\">游轮Komodo Dancer共有8间客舱，它最多可接纳16位乘客一同出海探奇，但船上却有17名船员，对所有来到这</span></span><span style=\"color:#64451D;line-height:1.5;\"><span style=\"color:#64451D;line-height:1.5;\">艘游轮上的旅客来说，这场游轮潜水之旅可谓是在养尊处优中度过。所有客舱都配有私人卫浴室，独立的空调系统，储物柜，毛巾，洗浴用品，吹风机等设施。其中1号客舱和8号客舱为主客舱，配有一张可调整床铺（大床或双床都可）和海景窗。2号客舱和3号客舱则为豪华双人客舱，配有一张双人床，浪漫的气氛特别适合爱侣同行的潜水爱好者。余下的4，5，6，7号客舱则为单人上下床客舱，每间客舱配有一张上下单人床，可共2人入住，特别适合单人出行</span></span><span style=\"color:#64451D;line-height:1.5;\"><span style=\"color:#64451D;line-height:1.5;\">的潜友。\r\n	<hr />\r\n	</span></span>\r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/11/b93f2a92-d4b5-4080-8aa2-5d57b603ecec.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/11/d159db05-a42a-4a69-92f5-573e3fa8a72d.png\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/11/db2e1187-2715-4f57-b788-de2b2efc2165.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/11/6a4f6f8a-c308-44f6-a170-cda549d084cc.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/11/7e37a67b-a530-4730-a095-5429c3332616.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/11/0edd99b4-ddb4-4098-ab00-affa9b39a50c.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/11/bb8ce57c-f97c-4aff-984b-b0f33d2f89b1.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/11/a97458d4-72c0-42ca-9809-2a876fb04a0e.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/11/34143d29-f471-47c1-9fc0-547b248104b5.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/11/351a3275-12d5-40c2-b1bb-fa8b2e08f59c.jpg\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"color:#64451D;line-height:1.5;\"><span style=\"color:#64451D;line-height:1.5;background-color:#FFFFFF;\"><span></span></span></span> \r\n</p>', null, 'uploadfiles/travel/travel_JqES1462930946461.jpg', '科莫多舞者', null, '', '| Komodo Dancer', '01768d64-6c80-4f7c-84dc-ce3b26064a1c', '科莫多', '', '印度尼西亚', '', 'Komodo Dancer', '0');
INSERT INTO `dive_travel` VALUES ('ff65cd56-d82a-4a0c-98c4-fed58cefa232', '2016-04-28 12:35:27', '<h1 style=\"text-align:center;\">\r\n	<b><span style=\"color:#337FE5;font-size:16px;\"><span style=\"font-size:32px;color:#000000;line-height:1;\"><img src=\"/attached/image/2016/05/12/403154c6-a444-4496-a8f4-3cce06306946.jpg\" alt=\"\" /><br />\r\n</span></span></b> \r\n</h1>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<b><span style=\"color:#000000;font-size:32px;line-height:1;\">Truk Master</span><span style=\"color:#337FE5;font-size:16px;line-height:1;\"></span><br />\r\n</b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:center;\">\r\n	<b><span style=\"color:#337FE5;font-size:32px;line-height:1;\"><span style=\"font-size:16px;\">特鲁克主人号</span><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船名：</span></b><span style=\"color:#64451D;\">特鲁克主人号</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">船体：</span></b><span style=\"color:#64451D;\">双层</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">目的地</span></b><b><strong><span style=\"color:#64451D;\">：</span></strong></b><span style=\"color:#64451D;\">特鲁克泻湖，楚克</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">舱位数量</span></b><b><span style=\"color:#333333;\">：</span></b><span style=\"color:#333333;\"></span><span style=\"color:#64451D;\">8</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">可容纳客人</span></b><b><span style=\"color:#333333;\">:</span><span style=\"color:#333333;\"> </span></b><span style=\"color:#333333;\"></span><span style=\"color:#64451D;\">16</span><b><span style=\"color:#333333;\"></span></b><span></span><span style=\"color:#64451D;\">位</span> \r\n</p>\r\n<div class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<hr />\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\">其它信息</span></b><b><span style=\"color:#333333;\">:<br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"color:#64451D;font-size:14px;\">Truk Master建于1974年，并于2015年进行了全面的修改和整修，该邮轮将在2016年中旬正式的对外开放其船宿服务。船上的公共休闲娱乐区域主要分布在主甲板和上层甲板，对于喜欢晒太阳的游客，该邮轮的露天驾驶平台将是晒太阳的好去处，配有户外休闲娱乐桌椅，以及一个阳光甲板和一个潜水甲板。</span> \r\n</p>\r\n<hr />\r\n<b><img src=\"/attached/image/2016/04/28/98947ba8-7ac3-4e6d-abb4-c8851a706e0f.jpg\" alt=\"\" /><br />\r\n</b> \r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/04/28/d17cd2b9-1f68-4621-9dd1-f232984dd823.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"http://www.e-diving.com.cn/attached/image/2016/04/28/aaa05c15-216d-4efc-b04e-1b154ac54ce4.jpg\" /><br />\r\n</span></b>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/04/28/1385771b-15d3-4f41-882b-f9fe5688d635.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/04/28/e65bb011-9843-4123-9199-58a5eb164fde.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/04/28/1f9d2438-aa1d-4449-930b-f6ccc81b0242.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/04/28/15936d78-1a0a-4a45-84dc-f2ba876bd245.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/04/28/18acd975-cf8f-4608-adf7-66b8ce78ac59.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><img src=\"/attached/image/2016/04/28/4a034959-c7d0-4ed5-bfae-3d2b2e807e9d.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<b><span style=\"color:#333333;\"><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span>&nbsp;</span> \r\n</p>', null, 'uploadfiles/travel/travel_lOlA1461818478333.jpg', '特鲁克主人号', null, '', '| Truk Master', '01768d64-6c80-4f7c-84dc-ce3b26064a1c', '特鲁克（楚克）', '', '密克罗尼西亚', '', 'Truk Master', '0');
